    <AntButton Type="primary" Loading="loading" OnClick=enterLoading>
        Click me!
    </AntButton>
    <AntButton
        Type="primary"
        Icon="poweroff"
    Loading="iconLoading"
    OnClick="enterIconLoading"
    >
    Click me!
    </AntButton>
